mapper.select:
select  from group_buying_activity g JOIN product p on g.productID = p.productID
where productName like '%'||#{product.product_name}||'%'
and activityStatus=#{activityStatus}
and groupStartDate >= #{groupStartDate}
and groupEndDate <= #{groupEndDate}

